import './Animation.css'
import TextTransition, { presets } from 'react-text-transition';
import {React,useEffect,useState,useCallback} from 'react'


const AnimationText = () => {
    const [ArabicText, setArabicText] = useState(0);
    const [EnglishText, setEnglishText] = useState(0);


    const TEXTA = ['احنا بتوع القماش '];
    const TEXTE = ['Hey Man How Are you'];

    useEffect(() => {
        const intervalArabic  = setInterval(() => setArabicText((index)  => index + 1),3000);
        const intervalEnglish = setInterval(() => setEnglishText((index) => index+1),3000)
        return () => clearTimeout(intervalArabic);
      }, []);
    return (
    <div className="MainAnimationText">
        <span className='WelcomText'>
        <TextTransition springConfig={presets.stiff}>{TEXTA[ArabicText % TEXTA.length]}</TextTransition>
        <TextTransition springConfig={presets.stiff}>{TEXTE[EnglishText % TEXTE.length]}</TextTransition>

        </span>
    </div>
    );
}
 
export default AnimationText;
