import './NavBar.css'
import { UilTruck } from '@iconscout/react-unicons'
import {React,useEffect,useState} from 'react'

const NavBar = () => {
    const [ArabicText,setArabicText]=useState(true)

    const Handelshipping=()=>{
setArabicText(curr=>!curr)
    }
    return (
    <div className="MainNav">

      <div className='TopNav' >
      <UilTruck/>
      {ArabicText?
             <span className='ArabicText'>شحن عادي 100 جنيه  | شحن سريع 200 جنيه -  شحن مجاني للطلبات التي تزيد عن 300 جنيه</span>
              :
             <span className='EnglishText'>Standard Shipping  99.99 EGP | Express Shipping   199.99 EGP - Free Shipping on Orders Over  300 EGP</span>
       }
      </div>
      <div className="DownNav"></div>
    </div>
    );
}
 
export default NavBar;
