import './NavBar.css'
import { UilTruck } from '@iconscout/react-unicons'
import {React,useEffect,useState,useCallback} from 'react'

const NavBar = () => {
    const names = [
        'شحن عادي 100 جنيه  | شحن سريع 200 جنيه -  شحن مجاني للطلبات التي تزيد عن 300 جنيه', 'Standard Shipping  99.99 EGP | Express Shipping   199.99 EGP - Free Shipping on Orders Over  300 EGP'
    ]
    
    const [ArabicText,setArabicText]=useState(true)

    const shuffle = useCallback(() => {
        const index = Math.floor(Math.random() * names.length);
        setArabicText(names[index]);
    }, []);

    useEffect(() => {
        const intervalID = setInterval(shuffle, 3000);
        return () => clearInterval(intervalID);
    }, [shuffle])


    return (
    <div className="MainNav">

      <div className='TopNav' >
      <UilTruck/><span className='ArabicText'>{ArabicText}</span>
      </div>
      <div className="DownNav"></div>
    </div>
    );
}
 
export default NavBar;
