import './NavBar.css'
import { UilTruck } from '@iconscout/react-unicons'
import {React,useEffect,useState} from 'react'

const NavBar = () => {
    const [ArabicText,setArabicText]=useState(true)

    const Handelshipping=()=>{
        setInterval(()=>{setArabicText(curr=>!curr)},5000)
    }
    useEffect(() => {
        const interval = setInterval(() => {
          console.log('This will be called every 2 seconds');
        }, 2000);
      
        return () => clearInterval(interval);
      }, []);
      
    return (
    <div className="MainNav">

      <div className='TopNav' onAnimationStart={Handelshipping}>
      <UilTruck/>
      {ArabicText?
             <span>شحن عادي 100 جنيه  | شحن سريع 200 جنيه -  شحن مجاني للطلبات التي تزيد عن 300 جنيه</span>
              :
             <span>Standard Shipping $14.99 | Express Shipping $24.99 - Free Shipping on Orders Over $300</span>
       }
      </div>
      <div className="DownNav"></div>
    </div>
    );
}
 
export default NavBar;
